import React, { useEffect, useState } from "react";
import "./App.css";


function App() {
  const [tarefas, setTarefas] = useState([]);
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");


  const carregarTarefas = async () => {
    try {
      const resposta = await fetch("http://localhost:8080/tarefas");
      const dados = await resposta.json();
      setTarefas(dados);
    } catch (erro) {
      console.error("Erro ao buscar tarefas:", erro);
    }
  };

  useEffect(() => {
    carregarTarefas();
  }, []);

  // --- Adicionar nova tarefa ---
  const adicionarTarefa = async (e) => {
    e.preventDefault();
    if (!titulo || !descricao) return alert("Preencha todos os campos!");

    const novaTarefa = {
      titulo,
      descricao,
      status: "afazer",
    };

    try {
      const resposta = await fetch("http://localhost:8080/tarefas", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(novaTarefa),
      });

      if (!resposta.ok) throw new Error("Erro ao adicionar tarefa");
      setTitulo("");
      setDescricao("");
      carregarTarefas();
    } catch (erro) {
      console.error(erro);
    }
  };

// --- Mover tarefa entre colunas ---
  const moverTarefa = async (id, novoStatus) => {
    try {
      const tarefa = tarefas.find((t) => t.id === id);
      if (!tarefa) return;

      const resposta = await fetch(`http://localhost:8080/tarefas/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...tarefa, status: novoStatus }),
      });

      if (!resposta.ok) throw new Error("Erro ao mover tarefa");
      carregarTarefas();
    } catch (erro) {
      console.error(erro);
    }
  };


  return (
  <div className="kanban-container">
    <h1>üìã Mini Kanban - Veritas</h1>

    {/* Formul√°rio */}
    <form className="kanban-form" onSubmit={adicionarTarefa}>
      <input
        type="text"
        placeholder="T√≠tulo"
        value={titulo}
        onChange={(e) => setTitulo(e.target.value)}
      />
      <input
        type="text"
        placeholder="Descri√ß√£o"
        value={descricao}
        onChange={(e) => setDescricao(e.target.value)}
      />
      <button type="submit">Adicionar</button>
    </form>

    {/* Colunas */}
      <div className="kanban-board">
        <div className="kanban-column">
          <h2>üïì A Fazer</h2>
          {tarefas
            .filter((t) => t.status === "afazer")
            .map((t) => (
              <div key={t.id} className="tarefa">
                <h4>{t.titulo}</h4>
                <p>{t.descricao}</p>
                <div className="botoes-mover">
                  <button onClick={() => moverTarefa(t.id, "emprogresso")}>
                    ‚û°Ô∏è Em Progresso
                  </button>
                  <button onClick={() => moverTarefa(t.id, "concluida")}>
                    ‚úÖ Conclu√≠da
                  </button>
                </div>
              </div>
            ))}
        </div>

        <div className="kanban-column">
          <h2>üèóÔ∏è Em Progresso</h2>
          {tarefas
            .filter((t) => t.status === "emprogresso")
            .map((t) => (
              <div key={t.id} className="tarefa">
                <h4>{t.titulo}</h4>
                <p>{t.descricao}</p>
                <div className="botoes-mover">
                  <button onClick={() => moverTarefa(t.id, "afazer")}>
                    ‚¨ÖÔ∏è A Fazer
                  </button>
                  <button onClick={() => moverTarefa(t.id, "concluida")}>
                    ‚úÖ Conclu√≠da
                  </button>
                </div>
              </div>
            ))}
        </div>

        <div className="kanban-column">
          <h2>‚úÖ Conclu√≠do</h2>
          {tarefas
            .filter((t) => t.status === "concluida")
            .map((t) => (
              <div key={t.id} className="tarefa">
                <h4>{t.titulo}</h4>
                <p>{t.descricao}</p>
                <div className="botoes-mover">
                  <button onClick={() => moverTarefa(t.id, "afazer")}>
                    ‚¨ÖÔ∏è A Fazer
                  </button>
                  <button onClick={() => moverTarefa(t.id, "emprogresso")}>
                    ‚û°Ô∏è Em Progresso
                  </button>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default App;
