import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [tarefas, setTarefas] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/tarefas")
      .then((res) => res.json())
      .then((data) => setTarefas(data))
      .catch(() => alert("Erro ao buscar tarefas"));
  }, []);

  const aFazer = tarefas.filter(t => !t.concluida && !t.titulo.toLowerCase().includes("progresso"));
  const emProgresso = tarefas.filter(t => !t.concluida && t.titulo.toLowerCase().includes("progresso"));
  const concluidas = tarefas.filter(t => t.concluida);

  const renderTarefas = (lista) => (
    lista.map((tarefa) => (
      <div key={tarefa.id} className="tarefa">
        <h4>{tarefa.titulo}</h4>
        <p>{tarefa.descricao}</p>
      </div>
    ))
  );

  return (
    <div className="kanban-container">
      <h1>ðŸ“‹ Mini Kanban - Veritas</h1>
      <div className="kanban-board">
        <div className="kanban-column">
          <h2>ðŸ•“ A Fazer</h2>
          {renderTarefas(aFazer)}
        </div>
        <div className="kanban-column">
          <h2>ðŸ”§ Em Progresso</h2>
          {renderTarefas(emProgresso)}
        </div>
        <div className="kanban-column">
          <h2>âœ… ConcluÃ­do</h2>
          {renderTarefas(concluidas)}
        </div>
      </div>
    </div>
  );
}

export default App;
