import React, { useEffect, useState } from "react";

function App() {
  const [tarefas, setTarefas] = useState([]);
  const [titulo, setTitulo] = useState("");
  const [descricao, setDescricao] = useState("");


  const carregarTarefas = async () => {
    try {
      const resposta = await fetch("http://localhost:8080/tarefas");
      const dados = await resposta.json();
      setTarefas(dados);
    } catch (erro) {
      console.error("Erro ao buscar tarefas:", erro);
    }
  };

  useEffect(() => {
    carregarTarefas();
  }, []);


  const adicionarTarefa = async (e) => {
    e.preventDefault();
    if (!titulo || !descricao) return alert("Preencha todos os campos!");

    const novaTarefa = {
      titulo,
      descricao,
      concluida: false,
    };

    try {
      const resposta = await fetch("http://localhost:8080/tarefas", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(novaTarefa),
      });

      if (!resposta.ok) throw new Error("Erro ao adicionar tarefa");
      setTitulo("");
      setDescricao("");
      carregarTarefas();
    } catch (erro) {
      console.error(erro);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>üìã Mini Kanban - Veritas</h1>

      {/* Formul√°rio */}
      <form onSubmit={adicionarTarefa} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="T√≠tulo"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          style={{ padding: "8px", marginRight: "8px" }}
        />
        <input
          type="text"
          placeholder="Descri√ß√£o"
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
          style={{ padding: "8px", marginRight: "8px" }}
        />
        <button
          type="submit"
          style={{
            padding: "8px 12px",
            background: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
          }}
        >
          Adicionar
        </button>
      </form>

      {/* Colunas */}
      <div style={{ display: "flex", gap: "20px" }}>
        <div style={{ flex: 1, background: "#f4f4f4", padding: "10px" }}>
          <h2>üïì A Fazer</h2>
          {tarefas
            .filter((t) => !t.concluida)
            .map((t) => (
              <div key={t.id} style={{ background: "#fff", padding: "10px", marginBottom: "8px" }}>
                <strong>{t.titulo}</strong>
                <p>{t.descricao}</p>
              </div>
            ))}
        </div>

        <div style={{ flex: 1, background: "#e7f1ff", padding: "10px" }}>
          <h2>üèóÔ∏è Em Progresso</h2>
          <p>(vazio por enquanto)</p>
        </div>

        <div style={{ flex: 1, background: "#d4edda", padding: "10px" }}>
          <h2>‚úÖ Conclu√≠do</h2>
          {tarefas
            .filter((t) => t.concluida)
            .map((t) => (
              <div key={t.id} style={{ background: "#fff", padding: "10px", marginBottom: "8px" }}>
                <strong>{t.titulo}</strong>
                <p>{t.descricao}</p>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default App;
